// Some Comments about this file

function open_href(url){
    document.location.href = url;
}

function HelloAlert ()
{
    alert("Hello World!")
}

function HelloBold ()
{
    document.write('<b>Hello World</b>');
}

function count(to)
{
    for (i = 0; i <= to; i++)
    {
        document.write("The number is " + i)
        document.write("<br />")
    }
}